import "./style.css";
import { useState } from "react";



const buttons = [
    '%', 'CE', 'C', 'del',
    '1/x', 'x*x', '6', '/',
    '7', '8', '9', '*',
    '4', '5', '6', '-',
    '1','2 ' , '3' , '+',
    '+/-' ,'0 ' , ' .', '='
];



export default function Calculator(){
    const [display, setDisplay] = useState("");
  

    return (
        <div className="calculator">
            <div className="display">{display}</div>
            <div className="buttons">
                {buttons.forEach((button) => (
                    <button key={button}>
                        {button}
                    </button>)
                )}
            </div>
        </div>
    );
}
