import React, { useState } from "react";
import "./style.css";

const buttons = [
  '%', 'CE', 'C', 'del',
  '1/x', 'x²', '√', '/',
  '7', '8', '9', '*',
  '4', '5', '6', '-',
  '1', '2', '3', '+',
  '+/-', '0', '.', '='
];
const symbols = ["+", "-", "*", "/", "%"];

export default function Calculator() {
  const [display, setDisplay] = useState("");

   const handleClick = (button) => {
     if (button === "=") {;
  //     evaluate();
     } else if (button === "C"||button === "CE") {
      setDisplay("");
    }else if(button === "del"){
      setDisplay(display.slice(0, -1));
     }else if (button === "+/-") {   
      let lastSymbolIndex = -1;

      for (let i = display.length - 1; i >= 0; i--) {
          if (symbols.includes(display[i])) {
              lastSymbolIndex = i;
              break;
          }
      }
      let startOfLastNumber = lastSymbolIndex === -1 ? 0 : lastSymbolIndex + 1;
  
      // Extract the last number from the display
      let lastNumber = display.substring(startOfLastNumber);
      
      // Check if last number is negative, if so, remove the minus sign
      if (lastNumber.startsWith('-')) {
          lastNumber = lastNumber.substring(1); // Remove the negative sign
      } else {
          lastNumber = '-' + lastNumber; // Add the negative sign
      }
  
      // Create the new display string
      const newDisplay = display.substring(0, startOfLastNumber) + lastNumber;
  
      // Update the state
      setDisplay(newDisplay);
  }else if (symbols.includes(button)) {
     
      if (!symbols.includes(display.slice(-1))) {
          setDisplay(display + button);
      }
     }
      else {
       setDisplay(display + button);
    }
   };

  // const evaluate = async () => {
  //   try {
  //     const response = await fetch("http://localhost:8080/api/calculate", {
  //       method: "POST",
  //       headers: {
  //         "Content-Type": "application/json"
  //       },
  //       body: JSON.stringify({ expression: display })
  //     });
  //     const result = await response.json();
  //     setDisplay(result.result);
  //   } catch (error) {
  //     console.error("Error evaluating the expression:", error);
  //   }
  // };

  return (
    <div className="calculator">
      <div className="display">{display}</div>
      <div className="buttons">
        {buttons.map((button) => (
          <button key={button} onClick={() => handleClick(button)}>
            {button}
          </button>
        ))}
      </div>
    </div>
  );
}
