import React, { useState } from "react";
import "./style.css";
import handleSingleOperation from "./calculatorFunctions/handleSingleOperation";
import handleOperator from "./calculatorFunctions/handleOperator";
import handleEquals from "./calculatorFunctions/handleEquals";
import handleClick from "./Functions/handleClick";

const buttons = [
  '%', 'CE', 'C', 'del',
  '1/x', 'x²', '√', '/',
  '7', '8', '9', '*',
  '4', '5', '6', '-',
  '1', '2', '3', '+',
  '+/-', '0', '.', '='
];

export default function Calculator() {
  const [display, setDisplay] = useState("");
  const [currentNumber, setCurrentNumber] = useState("");
  const [expression, setExpression] = useState("");

  return (
    <div className="calculator">
      <div className="display">{display}</div>
      <div className="buttons">
        {buttons.map((button) => (
          <button
            key={button}
            onClick={() =>
              handleClick(button, display, setDisplay, currentNumber, setCurrentNumber, expression, setExpression)
            }
          >
            {button}
          </button>
        ))}
      </div>
    </div>
  );
}
