import React, { useState } from "react";
import "./style.css";
import "./Functions/handleDelete"


const buttons = [
  '%', 'CE', 'C', 'del',
  '1/x', 'x²', '√', '/',
  '7', '8', '9', '*',
  '4', '5', '6', '-',
  '1', '2', '3', '+',
  '+/-', '0', '.', '='
];

export default function Calculator() {
  const [display, setDisplay] = useState("");
  const [currentNumber, setCurrentNumber] = useState("");
  const [expression, setExpression] = useState("");
  const [isResultDisplayed, setIsResultDisplayed] = useState(false);
  const handleClick = (button) => {
    if (button === "C" || button === "CE") {
      setDisplay("");
      setCurrentNumber("");
      setExpression("");
      setIsResultDisplayed(false);
    } else if (button === "del") {
      handleDelete(currentNumber, setCurrentNumber, display, setDisplay, expression, setExpression);
    } else if (!isNaN(button) && isResultDisplayed) {
      setDisplay(button);
      setCurrentNumber(button);
      setIsResultDisplayed(false);
    } else if (button === "+/-") {
      if (currentNumber) {
        const toggledNumber = (parseFloat(currentNumber) * -1).toString();
        setCurrentNumber(toggledNumber);
        setDisplay((prevDisplay) =>
          prevDisplay.slice(0, -currentNumber.length) + toggledNumber
        );
      }
    } else if (["+", "-", "*", "/"].includes(button)) {
      handleOperator(button, currentNumber, setExpression, setCurrentNumber, setDisplay);
      setIsResultDisplayed(false);
    } else if (["1/x", "x²", "√", "%"].includes(button)) {
      handleSingleOperation(button, currentNumber, setDisplay, setCurrentNumber, display);
    } else if (button === "=") {
      handleEquals(currentNumber, expression, setDisplay, setCurrentNumber, setExpression, setIsResultDisplayed);
    } else {
      setCurrentNumber(currentNumber + button);
      setDisplay(display + button);
      setIsResultDisplayed(false);
    }
  };


  return (
    <div className="calculator">
      <div className="display">{display}</div>
      <div className="buttons">
        {buttons.map((button) => (
          <button
            key={button}
            onClick={() =>
              handleClick(button, display, setDisplay, currentNumber, setCurrentNumber, expression, setExpression,isResultDisplayed, setIsResultDisplayed)
            }
          >
            {button}
          </button>
        ))}
      </div>
    </div>
  );
}
