import handleSingleOperation from "./handleSingleOperation";
import handleOperator from "./handleOperator";
import handleEquals from "./handleEquals";
import handleDelete from "./handleDelete";

const handleClick = (button, display, setDisplay, currentNumber, setCurrentNumber, expression, setExpression) => {
  if (button === "C" || button === "CE") {
    setDisplay("");
    setCurrentNumber("");
    setExpression("");
  } else if (button === "del") {
    handleDelete(currentNumber, setCurrentNumber, display, setDisplay, expression, setExpression);
  } else if (button === "+/-") {
    if (currentNumber) {
      const toggledNumber = (parseFloat(currentNumber) * -1).toString();
      setCurrentNumber(toggledNumber);
      setDisplay((prevDisplay) =>
        prevDisplay.slice(0, -currentNumber.length) + toggledNumber
      );
    }
  } else if (["+", "-", "*", "/"].includes(button)) {
    handleOperator(button, currentNumber, setExpression, setCurrentNumber, setDisplay);
  } else if (["1/x", "x²", "√", "%"].includes(button)) {
    handleSingleOperation(button, currentNumber, setDisplay, setCurrentNumber, display);
  } else if (button === "=") {
    handleEquals(currentNumber, expression, setDisplay, setCurrentNumber, setExpression);
  } else {
    setCurrentNumber(currentNumber + button);
    setDisplay(display + button);
  }
};

export default handleClick;
