import React, { useState } from "react";
import "./Calculator.css";

const buttons = ['%', 'CE', 'C', 'del', '1/x', 'x^2', '√x', '/', '7', '8', '9', '*', '4', '5', '6', '-', '1', '2', '3', '+', '+/-', '0', '.', '='];

export default function Calculator() {
  const [display, setDisplay] = useState("");

  const handleButtonClick = async (button) => {
    if (button === "=") {
      // Send expression for evaluation
      await sendCalculationRequest("evaluate", display);
    } else if (button === "C") {
      setDisplay("");
    } else if (button === "1/x") {
    
      await sendCalculationRequest("reciprocal", display);
    } else if (button === "x^2") {
     
      await sendCalculationRequest("square", display);
    } else if (button === "√x") {
      
      await sendCalculationRequest("sqrt", display);
    } else {
      setDisplay((prev) => prev + button);
    }
  };

  const sendCalculationRequest = async (operation, expression) => {
    try {
      const response = await fetch("http://localhost:8080/calculate", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ operation, expression })
      });
      const data = await response.json();
      setDisplay(data.result);
    } catch (error) {
      console.error("Calculation error:", error);
    }
  };

  return (
    <div className="calculator">
      <div className="display">{display}</div>
      <div className="buttons">
        {buttons.map((button) => (
          <button key={button} onClick={() => handleButtonClick(button)}>
            {button}
          </button>
        ))}
      </div>
    </div>
  );
}
