const handleOperator = (operator, currentNumber, setExpression, setCurrentNumber, setDisplay, setIsResultDisplayed) => {
  if (currentNumber === "") return;

  if(operator=='+'){setExpression((prev) => prev + currentNumber + operator);}
  if(operator=='−'){setExpression((prev) => prev + currentNumber + '-');}
  if(operator=='×'){setExpression((prev) => prev + currentNumber + operator);}
  else {setExpression((prev) => prev + currentNumber + operator);}
  setCurrentNumber("");
  setDisplay((prevDisplay) => prevDisplay + `${operator}`);
  setIsResultDisplayed(false);
};

export default handleOperator;
