import React, { useState } from "react";
import "./style.css";

const buttons = [
  '%', 'CE', 'C', 'del',
  '1/x', 'x²', '√', '/',
  '7', '8', '9', '*',
  '4', '5', '6', '-',
  '1', '2', '3', '+',
  '+/-', '0', '.', '='
];

export default function Calculator() {
  const [display, setDisplay] = useState("");
  const [currentNumber, setCurrentNumber] = useState("");
  const [expression, setExpression] = useState(""); 

  // const handleSingleOperation = (operation) => {
  //   let result;
  //   const number = parseFloat(currentNumber);

  //   if (isNaN(number)) return;

  //   switch (operation) {
  //     case "1/x":
  //       result = 1 / number;
  //       break;
  //     case "x²":
  //       result = number ** 2;
  //       break;
  //     case "√":
  //       result = Math.sqrt(number);
  //       break;
  //     case "%":
  //       result = number / 100;
  //       break;
  //     default:
  //       return;
  //   }

  //   result = result.toString();
  //   setCurrentNumber(result);
  //   setDisplay(display.replace(currentNumber, result));
  // };
  const handleSingleOperation = async (operation) => {
    const number = parseFloat(currentNumber);

    if (isNaN(number)) return;

    try {
        const response = await fetch(`http://localhost:8080/calculate/single-operation?operation=${operation}&number=${number}`);
        const result = await response.text();
        
        // Check for error response
        if (result.startsWith("Error")) {
            setDisplay(result); // Show error message in display
            return;
        }

        setCurrentNumber(result);
        const updatedDisplay = display.slice(0,-currentNumber.length) + `${result}`;
        setDisplay(updatedDisplay);
    } catch (error) {
        console.error("Error:", error);
        setDisplay("Error");
    }
};



  const handleOperator = (operator) => {
    if (currentNumber === "") return;

    setExpression((prev) => prev + currentNumber + operator);
    setCurrentNumber("");
    setDisplay((prevDisplay) => prevDisplay + ` ${operator} `);
  };

  const handleEquals = async () => {
    if (currentNumber === "" && expression === "") return;

    const fullExpression = expression + currentNumber;

    try {
      const response = await fetch(`http://localhost:8080/calculate/api?expression=${encodeURIComponent(fullExpression)}`);
      const result = await response.text();
      setDisplay(result);
      setCurrentNumber(result);
      setExpression("");
      console.log("Evaluated Expression:",fullExpression);  
      console.log(result);  
    } catch (error) {
      console.error("Evaluation Error:", error);     
      setDisplay("Error");
    }
  };

  const handleClick = (button) => {
    if (button === "C" || button === "CE") {
      setDisplay("");
      setCurrentNumber("");
      setExpression("");
    } else if (button === "del") {
      setCurrentNumber(currentNumber.slice(0, -1));
      setDisplay(display.slice(0, -1));
    } else if (button === "+/-") {
      if (currentNumber) {
        const toggledNumber = (parseFloat(currentNumber) * -1).toString();
        setCurrentNumber(toggledNumber);
        setDisplay((prevDisplay) =>
          prevDisplay.slice(0, -currentNumber.length) + toggledNumber
        );
      }
    } else if (["+", "-", "*", "/"].includes(button)) {
      handleOperator(button);
    } else if (button === "1/x" || button === "x²" || button === "√" || button === "%") {
      handleSingleOperation(button);
    } else if (button === "=") {
      handleEquals();
    } else {
      setCurrentNumber(currentNumber + button);
      setDisplay(display + button);
    }
  };

  return (
    <div className="calculator">
      <div className="display">{display}</div>
      <div className="buttons">
        {buttons.map((button) => (
          <button key={button} onClick={() => handleClick(button)}>
            {button}
          </button>
        ))}
      </div>
    </div>
  );
}
