import React, { useState } from "react";
import "./style.css";

const buttons = [
  '%', 'CE', 'C', 'del',
  '1/x', 'x²', '√', '/',
  '7', '8', '9', '*',
  '4', '5', '6', '-',
  '1', '2', '3', '+',
  '+/-', '0', '.', '='
];
const symbols = ["+", "-", "*", "/", "%"];

export default function Calculator() {
  const [display, setDisplay] = useState("");
  const handleReciprocal = () => {
    // Split the display string into parts
    const parts = display.split(/([\+\-\*\/])/).filter(Boolean); // Split by operators while keeping them

    // Check the last part, which should be the last number
    const lastPart = parts[parts.length - 1].trim();
    
    // If the last part is a number (or a negative number)
    if (!isNaN(lastPart)) {
        // Create the new expression with reciprocal
        const updatedDisplay = display.slice(0, display.length - lastPart.length) + ` - (1/${lastPart})`;
        setDisplay(updatedDisplay);
    }
};

   const handleClick = (button) => {
     if (button === "=") {;
  //     evaluate();
     } else if (button === "C"||button === "CE") {
      setDisplay("");
    }else if(button === "del"){
      setDisplay(display.slice(0, -1));
     }else if (button === "+/-") {   
      const lastNumberMatch = display.match(/-?\d+\.?\d*$/); 

      if (lastNumberMatch) {
          const lastNumber = lastNumberMatch[0];
          const newLastNumber = (parseFloat(lastNumber) * -1).toString();
          
          
          const newDisplay = display.slice(0, -lastNumber.length) + newLastNumber;
          setDisplay(newDisplay);
      }
  }else if (symbols.includes(button)) {
     
      if (!symbols.includes(display.slice(-1))) {
          setDisplay(display + button);
      }
     }
      else {
       setDisplay(display + button);
    }
   };

  // const evaluate = async () => {
  //   try {
  //     const response = await fetch("http://localhost:8080/api/calculate", {
  //       method: "POST",
  //       headers: {
  //         "Content-Type": "application/json"
  //       },
  //       body: JSON.stringify({ expression: display })
  //     });
  //     const result = await response.json();
  //     setDisplay(result.result);
  //   } catch (error) {
  //     console.error("Error evaluating the expression:", error);
  //   }
  // };

  return (
    <div className="calculator">
      <div className="display">{display}</div>
      <div className="buttons">
        {buttons.map((button) => (
          <button key={button} onClick={() => handleClick(button)}>
            {button}
          </button>
        ))}
      </div>
    </div>
  );
}
