import React, { useState } from "react";
import "./style.css";

const buttons = [
  '%', 'CE', 'C', '&#9003;',
  '1/x', 'x²', '√', '/',
  '7', '8', '9', '*',
  '4', '5', '6', '-',
  '1', '2', '3', '+',
  '+/-', '0', '.', '='
];

const symbols = ["+", "-", "*", "/", "%"];

export default function Calculator() {
  const [display, setDisplay] = useState("");

  const handleX = (button, power) => {
    if (display.length === 0) return;

    // Regex to split while preserving parentheses
    const parts = display.match(/(?:\([^()]*\)|[^\s()]+)/g);
    const lastPart = parts[parts.length - 1]?.trim() || "";

    // Handle "1/x"
    if (button === "1/x") {
      if (lastPart) {
        const updatedDisplay = display.slice(0, display.length - lastPart.length) + `(1/${lastPart})`;
        setDisplay(updatedDisplay);
      }
    } else {
      // Handle power operations
      if (lastPart) {
        const updatedDisplay = display.slice(0, display.length - lastPart.length) + `(${lastPart}^(${power}))`;
        setDisplay(updatedDisplay);
      }
    }
  };

  const handleClick = (button) => {
    if (button === "=") {
      // Uncomment to use evaluate function
      // evaluate();
    } else if (button === "C" || button === "CE") {
      setDisplay("");
    } else if (button === "del") {
      setDisplay(display.slice(0, -1));
    } else if (button === "+/-") {
      const lastNumberMatch = display.match(/-?\d+\.?\d*$/);

      if (lastNumberMatch) {
        const lastNumber = lastNumberMatch[0];
        const newLastNumber = (parseFloat(lastNumber) * -1).toString();
        const newDisplay = display.slice(0, -lastNumber.length) + newLastNumber;
        setDisplay(newDisplay);
      }
    } else if (symbols.includes(button)) {
      if (!symbols.includes(display.slice(-1))) {
        setDisplay(display + button);
      }
    } else if (button === "1/x") {
      handleX("1/x", -1);
    } else if (button === "x²") {
      handleX("x²", 2);
    } else if (button === "√") {
      handleX("√", 1 / 2);
    } else {
      setDisplay(display + button);
    }
  };

  return (
    <div className="calculator">
      <div className="display">{display}</div>
      <div className="buttons">
        {buttons.map((button) => (
          <button key={button} onClick={() => handleClick(button)}>
            {button}
          </button>
        ))}
      </div>
    </div>
  );
}
