import React, { useState } from "react";
import "./style.css";
import handleClick from "./Functions/handleClick";



const buttons = [
  '％', 'CE', 'C', '⌫',   // Percent sign, Clear Entry, Clear, Backspace
  '¹/ₓ', 'x²', '√', '÷',  // Reciprocal, Square, Square Root, Division
  '7', '8', '9', '×',     // Multiplication sign instead of *
  '4', '5', '6', '−',     // Minus sign instead of -
  '1', '2', '3', '+',     // Standard plus
  '±', '0', '.', '='      
];

export default function Calculator() {
  const [display, setDisplay] = useState("");
  const [currentNumber, setCurrentNumber] = useState("");
  const [expression, setExpression] = useState("");
  const [isResultDisplayed, setIsResultDisplayed] = useState(false);
  

  return (
    <div className="calculator">
      <div className="display">{display}</div>
      <div className="buttons">
        {buttons.map((button) => (
          <button
            key={button}
            onClick={() =>
              handleClick(button, display, setDisplay, currentNumber, setCurrentNumber, expression, setExpression,isResultDisplayed, setIsResultDisplayed)
            }
          >
            {button}
          </button>
        ))}
      </div>
    </div>
  );
}
