const handleOperator = (operator, currentNumber, setExpression, setCurrentNumber, setDisplay,isResultDisplayed, setIsResultDisplayed) => {
  if (currentNumber === "") return;

  setExpression((prev) => prev + currentNumber + operator);
  setCurrentNumber("");
  setDisplay((prevDisplay) => prevDisplay + `${operator}`);
  setIsResultDisplayed(false)
};

export default handleOperator;
